function MasterCtrl(t, e, h) {
    var o = 992;

    t.salesOrderCount = 0,
    t.customerCount = 0,
    t.itemCount = 0,

    t.failedSalesOrders = [],

    t.getWidth = function () {
        return window.innerWidth
    }, t.$watch(t.getWidth, function (g, n) {
        t.toggle = g >= o ? angular.isDefined(e.get("toggle")) ? e.get("toggle") ? !0 : !1 : !0 : !1
    }), t.toggleSidebar = function () {
        t.toggle = !t.toggle, e.put("toggle", t.toggle)
    }, window.onresize = function () {
        t.$apply()
    },

    t.getCustomersCount = function() {
        console.log('get customers called');
        return 888;
    },

    t.getSalesOrderCount = function() {
        //console.log('get getSalesOrderCount called');
        //
        //var apiUrl = location.href.replace(location.hash, '') + '&method=getSalesOrderCount';
        //$http.get(apiUrl)
        //    .success(function(response) {
        //        t.salesOrderCount = response[0].internalid;
        //    });

    },

    t.getItemsCount = function() {
        console.log('get getItemsCount called');
        return 888;
    };

    var apiUrl = location.href.replace(location.hash, '') + '&method=getSalesOrderCount';
    h.get(apiUrl)
        .success(function(response) {
            t.salesOrderCount = response[0].internalid;
        });

    apiUrl = location.href.replace(location.hash, '') + '&method=getCustomersCount';
    h.get(apiUrl)
        .success(function(response) {
            t.customerCount = response[0].internalid;
        });

    apiUrl = location.href.replace(location.hash, '') + '&method=getItemsCount';
    h.get(apiUrl)
        .success(function(response) {
            t.itemCount = response[0].internalid;
        });

    apiUrl = location.href.replace(location.hash, '') + '&method=getFailedSalesOrders';
    h.get(apiUrl)
        .success(function(response) {
            t.failedSalesOrders = response;
        });
}
angular.module("RDash").controller("MasterCtrl", ["$scope", "$cookieStore", "$http", MasterCtrl]);

function rdLoading() {
    var d = {
        restrict: "AE",
        template: '<div class="loading"><div class="double-bounce1"></div><div class="double-bounce2"></div></div>'
    };
    return d
}
angular.module("RDash").directive("rdLoading", rdLoading);

function rdWidgetBody() {
    var d = {
        requires: "^rdWidget",
        scope: {
            loading: "@?",
            classes: "@?"
        },
        transclude: !0,
        template: '<div class="widget-body" ng-class="classes"><rd-loading ng-show="loading"></rd-loading><div ng-hide="loading" class="widget-content" ng-transclude></div></div>',
        restrict: "E"
    };
    return d
}
angular.module("RDash").directive("rdWidgetBody", rdWidgetBody);

function rdWidgetFooter() {
    var e = {
        requires: "^rdWidget",
        transclude: !0,
        template: '<div class="widget-footer" ng-transclude></div>',
        restrict: "E"
    };
    return e
}
angular.module("RDash").directive("rdWidgetFooter", rdWidgetFooter);

function rdWidgetTitle() {
    var i = {
        requires: "^rdWidget",
        scope: {
            title: "@",
            icon: "@"
        },
        transclude: !0,
        template: '<div class="widget-header"><div class="row"><div class="pull-left"><i class="fa" ng-class="icon"></i> {{title}} </div><div class="pull-right col-xs-6 col-sm-4" ng-transclude></div></div></div>',
        restrict: "E"
    };
    return i
}
angular.module("RDash").directive("rdWidgetHeader", rdWidgetTitle);

function rdWidget() {
    var d = {
        transclude: !0,
        template: '<div class="widget" ng-transclude></div>',
        restrict: "EA"
    };
    return d
}
angular.module("RDash").directive("rdWidget", rdWidget);